        assertTrue(result);
        Message receivedMessage = receiverHandler.getLastMessage();
        assertNotNull(receivedMessage);
        assertEquals(senderAddress, receivedMessage.getFrom());
        assertEquals(receiverAddress, receivedMessage.getTo());
