        heap.insert(5);
        heap.insert(3);
        heap.insert(8);
        heap.insert(1);

        assertEquals(8, heap.peekRoot());
        assertEquals(8, heap.deleteRoot());
        assertEquals(5, heap.deleteRoot());
        assertEquals(3, heap.deleteRoot());
        assertEquals(1, heap.deleteRoot());
    }

