    public EnhancedBinaryHeap(Comparable[] items, boolean isMinHeap) {
        this.isMinHeap = isMinHeap;
        this.heap = new ArrayList<>();
        this.vertices = new ArrayList<>();

        // Initialize heap with items (using dummy vertices)
        for (int i = 0; i < items.length; i++) {
            heap.add(items[i]);
            vertices.add(new int[]{0, 0}); // Dummy vertices
        }

        // Build heap in O(n) time
        buildHeap();
    }

    public void insert(Comparable element, int v1, int v2) {
        heap.add(element);
        vertices.add(new int[]{v1, v2});
        heapifyUp(heap.size() - 1);
    }

