    public void insert(Comparable element) {
        insert(element, 0, 0);
    }

    public Comparable deleteRoot() {
        if (isEmpty()) {
            throw new IllegalStateException("Heap is empty");
        }
        Comparable root = heap.get(0);
        Comparable lastElement = heap.remove(heap.size() - 1);
        int[] lastVertices = vertices.remove(vertices.size() - 1); // Remove the vertex pair for the last element
        if (!isEmpty()) {
            heap.set(0, lastElement);
            vertices.set(0, lastVertices); // Move the last vertex pair to root
            heapifyDown(0);
        }
        return root;
    }

    public Comparable peekRoot() {
        if (isEmpty()) {
            throw new IllegalStateException("Heap is empty");
        }
        return heap.get(0);
    }

    public int[] peekRootVertices() {
