        vertices.clear();
    }

    // New heapSort method
    public Comparable[] heapSort() {
        List<Comparable> sortedList = new ArrayList<>();
        while (!isEmpty()) {
            sortedList.add(deleteRoot());
        }
        return sortedList.toArray(new Comparable[0]);
    }

    private void buildHeap() {
        // Start from the last non-leaf node and heapify down
        for (int i = (heap.size() / 2) - 1; i >= 0; i--) {
