        int parentIndex = (index - 1) / 2;
        if (compare(heap.get(parentIndex), heap.get(index))) {
            Collections.swap(heap, parentIndex, index);
            Collections.swap(vertices, parentIndex, index);
            heapifyUp(parentIndex);
        }
    }

    private void heapifyDown(int index) {
        int leftChildIndex = 2 * index + 1;
        int rightChildIndex = 2 * index + 2;
        int extreme = index; // Index of smallest (for min-heap) or largest (for max-heap)

        if (leftChildIndex < heap.size() && compare(heap.get(extreme), heap.get(leftChildIndex))) {
