        int extreme = index; // Index of smallest (for min-heap) or largest (for max-heap)

        if (leftChildIndex < heap.size() && compare(heap.get(extreme), heap.get(leftChildIndex))) {
            extreme = leftChildIndex;
        }

        if (rightChildIndex < heap.size() && compare(heap.get(extreme), heap.get(rightChildIndex))) {
            extreme = rightChildIndex;
        }

        if (extreme != index) {
            Collections.swap(heap, index, extreme);
            Collections.swap(vertices, index, extreme);
            heapifyDown(extreme);
        }
    }

    // New validateHeap method
    public boolean validateHeap() throws NoSuchElementException {
        if (isEmpty()) {
            throw new NoSuchElementException("Cannot validate empty heap");
        }
        return validateHeapHelper(0);
    }

    private boolean validateHeapHelper(int index) {
        // Base case: if it's a leaf node, it's valid
        if (index >= heap.size()) {
            return true;
        }

        int leftChildIndex = 2 * index + 1;
        int rightChildIndex = 2 * index + 2;

        // Check left child
        if (leftChildIndex < heap.size()) {
            if (compare(heap.get(index), heap.get(leftChildIndex))) {
                return false; // Heap property violated
            }
            if (!validateHeapHelper(leftChildIndex)) {
                return false; // Recursively check subtree
            }
        }

        // Check right child
        if (rightChildIndex < heap.size()) {
            if (compare(heap.get(index), heap.get(rightChildIndex))) {
                return false; // Heap property violated
            }
            if (!validateHeapHelper(rightChildIndex)) {
                return false; // Recursively check subtree
            }
        }

        return true; // All checks passed
    }
}

