import java.util.*;

class PaginationProcessor<T> {
    private List<T> dataset;
    private int defaultPageSize;

    public PaginationProcessor(List<T> dataset, int defaultPageSize) {
        this.dataset = dataset != null ? dataset : new ArrayList<>();
        this.defaultPageSize = defaultPageSize;
    }

    public PaginationResult<T> getPaginatedData(int pageNumber, int itemsPerPage) {
        if (pageNumber < 1) {
            throw new IllegalArgumentException("Page number must be 1 or greater");
        }
        if (itemsPerPage < 1) {
            throw new IllegalArgumentException("Items per page must be 1 or greater");
        }

        int totalItems = dataset.size();
        int totalPages = (int) Math.ceil((double) totalItems / itemsPerPage);

        int startIndex = (pageNumber - 1) * itemsPerPage;
        int endIndex = Math.min(startIndex + itemsPerPage, totalItems);

        List<T> pageData;
        if (startIndex >= totalItems) {
            pageData = new ArrayList<>();
        } else {
            pageData = dataset.subList(startIndex, endIndex);
        }

        return new PaginationResult<>(pageData, pageNumber, itemsPerPage, totalPages, totalItems);
    }

    public static class PaginationResult<T> {
        private List<T> data;
        private int currentPage;
        private int itemsPerPage;
        private int totalPages;
        private int totalItems;

        public PaginationResult(List<T> data, int currentPage, int itemsPerPage, int totalPages, int totalItems) {
            this.data = data;
            this.currentPage = currentPage;
            this.itemsPerPage = itemsPerPage;
            this.totalPages = totalPages;
            this.totalItems = totalItems;
        }

        public List<T> getData() { return data; }
        public int getCurrentPage() { return currentPage; }
        public int getItemsPerPage() { return itemsPerPage; }
        public int getTotalPages() { return totalPages; }
        public int getTotalItems() { return totalItems; }
    }
}

