import java.util.*;

class PaginationProcessor<T> {
    private List<T> dataset;
    private int defaultPageSize;

    public PaginationProcessor(List<T> dataset, int defaultPageSize) {
        this.dataset = dataset != null ? dataset : new ArrayList<>();
        this.defaultPageSize = defaultPageSize;
    }

    public PaginationResult<T> getPaginatedData(int pageNumber, int itemsPerPage) {
        if (pageNumber < 1) {
            throw new IllegalArgumentException("Page number must be 1 or greater");
        }
        if (itemsPerPage < 1) {
            throw new IllegalArgumentException("Items per page must be 1 or greater");
        }

        // Handle empty dataset case
        if (dataset.isEmpty()) {
            return new PaginationResult<>(new ArrayList<>(), pageNumber, itemsPerPage, 0, 0);
        }

        int totalItems = dataset.size();
        int totalPages = (int) Math.ceil((double) totalItems / itemsPerPage);

        // Auto-adjust page number if it exceeds total pages
        int effectivePageNumber = pageNumber;
        if (totalPages > 0 && pageNumber > totalPages) {
            effectivePageNumber = totalPages;
        }

        int startIndex = (effectivePageNumber - 1) * itemsPerPage;
        int endIndex = Math.min(startIndex + itemsPerPage, totalItems);

        List<T> pageData;
        if (startIndex >= totalItems) {
            pageData = new ArrayList<>();
        } else {
            pageData = dataset.subList(startIndex, endIndex);
        }

        return new PaginationResult<>(pageData, effectivePageNumber, itemsPerPage, totalPages, totalItems);
    }

    public PaginationResult<T> getPaginatedData(int pageNumber, Integer itemsPerPage) {
        if (pageNumber < 1) {
            throw new IllegalArgumentException("Page number must be 1 or greater");
        }
        if (itemsPerPage != null && itemsPerPage < 1) {
            throw new IllegalArgumentException("Items per page must be 1 or greater");
        }

        int effectiveItemsPerPage = itemsPerPage != null ? itemsPerPage : defaultPageSize;

        // Handle empty dataset case
        if (dataset.isEmpty()) {
            return new PaginationResult<>(new ArrayList<>(), pageNumber, effectiveItemsPerPage, 0, 0);
        }

        int totalItems = dataset.size();
        int totalPages = (int) Math.ceil((double) totalItems / effectiveItemsPerPage);

        // Auto-adjust page number if it exceeds total pages
        int effectivePageNumber = pageNumber;
        if (totalPages > 0 && pageNumber > totalPages) {
            effectivePageNumber = totalPages;
        }

        int startIndex = (effectivePageNumber - 1) * effectiveItemsPerPage;
        int endIndex = Math.min(startIndex + effectiveItemsPerPage, totalItems);

        List<T> pageData;
        if (startIndex >= totalItems) {
            pageData = new ArrayList<>();
        } else {
            pageData = dataset.subList(startIndex, endIndex);
        }

        return new PaginationResult<>(pageData, effectivePageNumber, effectiveItemsPerPage, totalPages, totalItems);
    }

    public static class PaginationResult<T> {
        private List<T> data;
        private int currentPage;
        private int itemsPerPage;
        private int totalPages;
        private int totalItems;

        public PaginationResult(List<T> data, int currentPage, int itemsPerPage, int totalPages, int totalItems) {
            this.data = data;
            this.currentPage = currentPage;
            this.itemsPerPage = itemsPerPage;
            this.totalPages = totalPages;
            this.totalItems = totalItems;
        }

        public List<T> getData() { return data; }
        public int getCurrentPage() { return currentPage; }
        public int getItemsPerPage() { return itemsPerPage; }
        public int getTotalPages() { return totalPages; }
        public int getTotalItems() { return totalItems; }

        @Override
        public String toString() {
            if (totalItems == 0) {
                return "Page " + currentPage + " of " + totalPages + " (items 0-0 of 0)";
            }

            int startItem = (currentPage - 1) * itemsPerPage + 1;
            int endItem = Math.min(startItem + data.size() - 1, totalItems);

            return "Page " + currentPage + " of " + totalPages + " (items " + startItem + "-" + endItem + " of " + totalItems + ")";
        }
    }
}

