import java.util.*;

class PaginationManager {
    public PaginationResult calculatePagination(int totalItems, int pageSize, int currentPage) {
        if (totalItems < 0) {
            throw new IllegalArgumentException("totalItems cannot be negative");
        }
        if (pageSize <= 0) {
            throw new IllegalArgumentException("pageSize must be positive");
        }
        if (currentPage <= 0) {
            throw new IllegalArgumentException("currentPage must be positive");
        }

        // Handle zero totalItems case
        if (totalItems == 0) {
            return new PaginationResult(0, pageSize, 1, 0, 0, 0, new ArrayList<>());
        }

        int totalPages = (int) Math.ceil((double) totalItems / pageSize);

        // Cap currentPage at totalPages if it exceeds
        int adjustedCurrentPage = Math.min(currentPage, totalPages);

        int startItem = (adjustedCurrentPage - 1) * pageSize + 1;
        int endItem = Math.min(adjustedCurrentPage * pageSize, totalItems);

        List<Integer> visiblePages = calculateVisiblePages(adjustedCurrentPage, totalPages);

        return new PaginationResult(totalItems, pageSize, adjustedCurrentPage, totalPages, startItem, endItem, visiblePages);
    }

    private List<Integer> calculateVisiblePages(int currentPage, int totalPages) {
        List<Integer> visiblePages = new ArrayList<>();

        if (totalPages <= 7) {
            // Show all pages if total pages is 7 or less
            for (int i = 1; i <= totalPages; i++) {
                visiblePages.add(i);
            }
        } else {
            // Always include first page
            visiblePages.add(1);

            // Calculate the range around current page (2 before and 2 after)
            int rangeStart = Math.max(1, currentPage - 2);
            int rangeEnd = Math.min(totalPages, currentPage + 2);

            // Add ellipsis after first page if there's a gap
            if (rangeStart > 2) {
                visiblePages.add(-1);
            }

            // Add the range around current page
            for (int i = rangeStart; i <= rangeEnd; i++) {
                if (i != 1 && i != totalPages) { // Don't duplicate first/last pages
                    visiblePages.add(i);
                }
            }

            // Add ellipsis before last page if there's a gap
            if (rangeEnd < totalPages - 1) {
                visiblePages.add(-1);
            }

            // Always include last page
            if (totalPages > 1) {
                visiblePages.add(totalPages);
            }
        }

        return visiblePages;
    }
}

class PaginationResult {
    private int totalItems;
    private int pageSize;
    private int currentPage;
    private int totalPages;
    private int startItem;
    private int endItem;
    private List<Integer> visiblePages;

    public PaginationResult(int totalItems, int pageSize, int currentPage, int totalPages, int startItem, int endItem, List<Integer> visiblePages) {
        this.totalItems = totalItems;
        this.pageSize = pageSize;
        this.currentPage = currentPage;
        this.totalPages = totalPages;
        this.startItem = startItem;
        this.endItem = endItem;
        this.visiblePages = visiblePages;
    }

    public int getTotalItems() { return totalItems; }
    public int getPageSize() { return pageSize; }
    public int getCurrentPage() { return currentPage; }
    public int getTotalPages() { return totalPages; }
    public int getStartItem() { return startItem; }
    public int getEndItem() { return endItem; }
    public List<Integer> getVisiblePages() { return visiblePages; }
}

