class State {
    private String name;
    private boolean isFinal;
    private Map<Character, State> transitions;

    public State(String name, boolean isFinal) {
        this.name = name;
        this.isFinal = isFinal;
        this.transitions = new HashMap<>();
    }

    public String getName() {
        return name;
    }

    public boolean isFinal() {
