import java.util.*;

class State {
    private String name;
    private boolean isFinal;
    private Map<Character, State> transitions;

    public State(String name, boolean isFinal) {
        this.name = name;
        this.isFinal = isFinal;
        this.transitions = new HashMap<>();
    }

    public String getName() {
        return name;
    }

    public boolean isFinal() {
        return isFinal;
    }

    public void addTransition(char symbol, State nextState) {
        transitions.put(symbol, nextState);
    }

    public State getNextState(char symbol) {
        return transitions.get(symbol);
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();
        sb.append(name).append(": ");

        List<Character> sortedSymbols = new ArrayList<>(transitions.keySet());
        Collections.sort(sortedSymbols);

        for (int i = 0; i < sortedSymbols.size(); i++) {
            char symbol = sortedSymbols.get(i);
            State nextState = transitions.get(symbol);
            sb.append("--").append(symbol).append("-> ").append(nextState.getName());
            if (i < sortedSymbols.size() - 1) {
                sb.append(" ");
            }
        }

        return sb.toString();
    }
}

class DFA {
    private List<State> states;
    private State initialState;
    private Set<Character> alphabet;

    public DFA() {
        this.states = new ArrayList<>();
        this.initialState = null;
        this.alphabet = new HashSet<>();
    }

    public void addState(State state) {
        states.add(state);
        if (initialState == null) {
            initialState = state;
        }
    }

    public void addTransition(State fromState, char symbol, State toState) {
        fromState.addTransition(symbol, toState);
        alphabet.add(symbol);
    }

    public Set<Character> getAlphabet() {
        return new HashSet<>(alphabet);
    }

    public boolean accepts(String input) {
        if (initialState == null) {
            return false;
        }

        State currentState = initialState;

        for (char c : input.toCharArray()) {
            State nextState = currentState.getNextState(c);
            if (nextState == null) {
                return false;
            }
            currentState = nextState;
        }

        return currentState.isFinal();
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();

        // Number of states
        sb.append("States: ").append(states.size()).append("\n");

        // Alphabet
        sb.append("Alphabet: ");
        List<Character> sortedAlphabet = new ArrayList<>(alphabet);
        Collections.sort(sortedAlphabet);
        sb.append(sortedAlphabet).append("\n");

        // Initial state
        sb.append("Initial state: ").append(initialState != null ? initialState.getName() : "null").append("\n");

        // Final states
        sb.append("Final states: [");
        List<String> finalStates = new ArrayList<>();
        for (State state : states) {
            if (state.isFinal()) {
                finalStates.add(state.getName());
            }
        }
        Collections.sort(finalStates);
        for (int i = 0; i < finalStates.size(); i++) {
            sb.append(finalStates.get(i));
            if (i < finalStates.size() - 1) {
                sb.append(", ");
            }
        }
        sb.append("]\n");

        // All transitions
        sb.append("Transitions:\n");
        for (State state : states) {
            sb.append(state.toString()).append("\n");
        }

        return sb.toString();
    }
}

