        BSTOperations.TreeNode root = new BSTOperations.TreeNode(1);
        assertEquals("[1]", bst.levelOrder(root));
    }

    @Test
    public void testLevelOrderWithNullsAndTrailingNullRemoval() {
        BSTOperations bst = new BSTOperations();
        // Tree: 1 -> left: 2, right: 3 -> 3 has left: 4
        BSTOperations.TreeNode root = new BSTOperations.TreeNode(1);
        root.left = new BSTOperations.TreeNode(2);
        root.right = new BSTOperations.TreeNode(3);
        root.right.left = new BSTOperations.TreeNode(4);

