        this.password = password;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }
}

class UserManager {
    private List<User> users;
    private Map<String, Integer> failedAttempts;
    private Set<String> lockedAccounts;

    public UserManager() {
        this.users = new ArrayList<>();
        this.failedAttempts = new HashMap<>();
        this.lockedAccounts = new HashSet<>();
    }

    public Map<String, Object> registerUser(User user) {
        Map<String, Object> result = new HashMap<>();

        // Check if username or password are empty
        if (user.getUsername() == null || user.getUsername().trim().isEmpty()) {
            result.put("success", false);
            result.put("message", "Username cannot be empty");
            return result;
        }

        if (user.getPassword() == null || user.getPassword().trim().isEmpty()) {
            result.put("success", false);
            result.put("message", "Password cannot be empty");
            return result;
        }

        // Check if username is unique (case-insensitive)
        for (User existingUser : users) {
            if (existingUser.getUsername().equalsIgnoreCase(user.getUsername())) {
                result.put("success", false);
                result.put("message", "Username already exists");
                return result;
            }
        }

        // Add user to the list
        users.add(user);
        result.put("success", true);
        result.put("message", "User registered successfully");
        return result;
    }

    public Map<String, Object> loginUser(String username, String password) {
        Map<String, Object> result = new HashMap<>();

        // Check if account is locked
        if (lockedAccounts.contains(username)) {
            result.put("success", false);
            result.put("message", "Account is locked");
            return result;
        }

        // Find user by username (case-insensitive)
        User foundUser = null;
        for (User user : users) {
            if (user.getUsername().equalsIgnoreCase(username)) {
                foundUser = user;
                break;
            }
        }

        // Check if user exists and password matches
        if (foundUser != null && foundUser.getPassword().equals(password)) {
            // Successful login - reset failed attempts
            failedAttempts.remove(username);
            result.put("success", true);
            result.put("message", "Login successful");
            result.put("user", foundUser);
        } else {
            // Failed login - increment attempts
            int attempts = failedAttempts.getOrDefault(username, 0) + 1;
            failedAttempts.put(username, attempts);

            if (attempts >= 3) {
                // Lock account after 3 failed attempts
                lockedAccounts.add(username);
                result.put("success", false);
                result.put("message", "Account is locked");
            } else {
                // Still have attempts remaining
                int remaining = 3 - attempts;
                result.put("success", false);
                result.put("message", "Invalid credentials");
                result.put("attemptsRemaining", remaining);
            }
        }

        return result;
    }
}

