        assertTrue((Boolean) result.get("success"));
        assertEquals("Login successful", result.get("message"));
        assertNotNull(result.get("user"));
        User returnedUser = (User) result.get("user");
        assertEquals("testuser", returnedUser.getUsername());
    }

    @Test
    void testFailedLoginWithAttemptsRemaining() {
        Map<String, Object> result = userManager.loginUser("testuser", "wrongpassword");

        assertFalse((Boolean) result.get("success"));
        assertEquals("Invalid credentials", result.get("message"));
        assertEquals(2, result.get("attemptsRemaining"));
        assertNull(result.get("user"));
    }

    @Test
    void testAccountLockAfterThreeFailedAttempts() {
        // First failed attempt
        Map<String, Object> result1 = userManager.loginUser("testuser", "wrong1");
        assertFalse((Boolean) result1.get("success"));
        assertEquals(2, result1.get("attemptsRemaining"));

        // Second failed attempt
        Map<String, Object> result2 = userManager.loginUser("testuser", "wrong2");
        assertFalse((Boolean) result2.get("success"));
        assertEquals(1, result2.get("attemptsRemaining"));

        // Third failed attempt - should lock account
        Map<String, Object> result3 = userManager.loginUser("testuser", "wrong3");
        assertFalse((Boolean) result3.get("success"));
        assertEquals("Account is locked", result3.get("message"));
        assertNull(result3.get("attemptsRemaining"));
    }

    @Test
    void testLoginAfterAccountLocked() {
        // Lock the account first
        userManager.loginUser("testuser", "wrong1");
        userManager.loginUser("testuser", "wrong2");
        userManager.loginUser("testuser", "wrong3");

        // Try to login with correct credentials after lock
        Map<String, Object> result = userManager.loginUser("testuser", "password123");
        assertFalse((Boolean) result.get("success"));
        assertEquals("Account is locked", result.get("message"));
    }

