import java.util.*;

class PathFinder {
    public static class PathAnalysis {
        public final int totalPaths;
        public final int maxPathLength;
        public final int minPathLength;
        public final List<String> samplePath;

        public PathAnalysis(int totalPaths, int maxPathLength, int minPathLength, List<String> samplePath) {
            this.totalPaths = totalPaths;
            this.maxPathLength = maxPathLength;
            this.minPathLength = minPathLength;
            this.samplePath = samplePath;
        }

        @Override
        public String toString() {
            return String.format("PathAnalysis{totalPaths=%d, maxPathLength=%d, minPathLength=%d, samplePath=%s}", 
                               totalPaths, maxPathLength, minPathLength, samplePath);
        }
    }

    public PathAnalysis findUniquePathsWithAnalysis(int[][] grid) {
        if (grid == null || grid.length == 0 || grid[0].length == 0) {
            return new PathAnalysis(0, 0, 0, new ArrayList<>());
        }

        int rows = grid.length;
        int cols = grid[0].length;

        // Check if start or end cell is an obstacle
        if (grid[0][0] == 1 || grid[rows-1][cols-1] == 1) {
            return new PathAnalysis(0, 0, 0, new ArrayList<>());
        }

        // dp[i][j] represents number of unique paths to reach cell (i, j)
        int[][] dp = new int[rows][cols];

        // Initialize first cell
        dp[0][0] = 1;

        // Initialize first row
        for (int j = 1; j < cols; j++) {
            if (grid[0][j] == 1) {
                dp[0][j] = 0; // Obstacle blocks this and all subsequent cells in this row
                // All subsequent cells in first row will remain 0
                break;
            } else {
                dp[0][j] = dp[0][j-1]; // Can reach if previous cell was reachable
            }
        }

        // Initialize first column
        for (int i = 1; i < rows; i++) {
            if (grid[i][0] == 1) {
                dp[i][0] = 0; // Obstacle blocks this and all subsequent cells in this column
                // All subsequent cells in first column will remain 0
                break;
            } else {
                dp[i][0] = dp[i-1][0]; // Can reach if previous cell was reachable
            }
        }

        // Fill the dp table
        for (int i = 1; i < rows; i++) {
            for (int j = 1; j < cols; j++) {
                if (grid[i][j] == 1) {
                    dp[i][j] = 0; // Obstacle, no paths through this cell
                } else {
                    dp[i][j] = dp[i-1][j] + dp[i][j-1];
                }
            }
        }

        // Calculate path length (from top-left to bottom-right)
        int pathLength = rows + cols - 2;

        // Generate a sample path as coordinates, avoiding obstacles
        List<String> samplePath = new ArrayList<>();
        if (dp[rows-1][cols-1] > 0) {
            // Use backtracking to find a valid path
            int currentRow = rows - 1;
            int currentCol = cols - 1;

            while (currentRow > 0 || currentCol > 0) {
                samplePath.add(0, "(" + currentRow + "," + currentCol + ")");

                // Choose to go up or left based on which cell has paths
                if (currentRow == 0) {
                    // Can only go left
                    currentCol--;
                } else if (currentCol == 0) {
                    // Can only go up
                    currentRow--;
                } else {
                    // Choose based on which direction has valid paths
                    if (dp[currentRow-1][currentCol] > 0) {
                        currentRow--; // Go up
                    } else {
                        currentCol--; // Go left
                    }
                }
            }
            samplePath.add(0, "(0,0)"); // Add starting position
        }

        return new PathAnalysis(dp[rows-1][cols-1], pathLength, pathLength, samplePath);
    }
}

