import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.HashMap;
import java.util.Map;

class TimeDifferenceCalculator {
    private static final String DEFAULT_DATETIME_PATTERN = "yyyy-MM-dd HH:mm:ss";

    public static Map<String, Object> calculateDetailedTimeDifference(String earlierTime, String laterTime) 
            throws ParseException {
        SimpleDateFormat formatter = new SimpleDateFormat(DEFAULT_DATETIME_PATTERN);
        formatter.setLenient(false); // Ensure strict parsing

        Date earlier = formatter.parse(earlierTime);
        Date later = formatter.parse(laterTime);

        long diffInMillis = Math.abs(later.getTime() - earlier.getTime());

        long seconds = diffInMillis / 1000;
        long minutes = seconds / 60;
        long hours = minutes / 60;
        long days = hours / 24;
        long weeks = days / 7;

        String humanReadable;
        if (minutes < 1) {
            humanReadable = "just now";
        } else if (hours < 1) {
            humanReadable = minutes == 1 ? "1 minute ago" : minutes + " minutes ago";
        } else if (days < 1) {
            humanReadable = hours == 1 ? "1 hour ago" : hours + " hours ago";
        } else if (weeks < 1) {
            humanReadable = days == 1 ? "1 day ago" : days + " days ago";
        } else if (weeks <= 4) {
            humanReadable = weeks == 1 ? "1 week ago" : weeks + " weeks ago";
        } else {
            humanReadable = "more than a month ago";
        }

        Map<String, Object> result = new HashMap<>();
        result.put("seconds", seconds);
        result.put("minutes", minutes);
        result.put("hours", hours);
        result.put("days", days);
        result.put("weeks", weeks);
        result.put("humanReadable", humanReadable);

        return result;
    }
}

