import java.util.*;

class EggDropSolver {
    public static class Result {
        public final int dpResult;
        public final int mathResult;
        public final int binarySearchResult;
        public final long dpTime;
        public final long mathTime;
        public final long binarySearchTime;

        public Result(int dpResult, int mathResult, int binarySearchResult, 
                     long dpTime, long mathTime, long binarySearchTime) {
            this.dpResult = dpResult;
            this.mathResult = mathResult;
            this.binarySearchResult = binarySearchResult;
            this.dpTime = dpTime;
            this.mathTime = mathTime;
            this.binarySearchTime = binarySearchTime;
        }
    }

    public static Result solveEggDrop(int k, int n) {
        // Validate input parameters
        if (k <= 0) {
            throw new IllegalArgumentException("Number of eggs must be positive");
        }
        if (n <= 0) {
            throw new IllegalArgumentException("Number of floors must be positive");
        }

        // Time the DP solution
        long dpStartTime = System.nanoTime();
        int dpResult = solveDynamicProgramming(k, n);
        long dpEndTime = System.nanoTime();
        long dpTime = dpEndTime - dpStartTime;

        // Time the mathematical solution
        long mathStartTime = System.nanoTime();
        int mathResult = solveMathematical(k, n);
        long mathEndTime = System.nanoTime();
        long mathTime = mathEndTime - mathStartTime;

        // Time the binary search with memoization solution
        long binarySearchStartTime = System.nanoTime();
        int binarySearchResult = solveBinarySearchWithMemoization(k, n);
        long binarySearchEndTime = System.nanoTime();
        long binarySearchTime = binarySearchEndTime - binarySearchStartTime;

        return new Result(dpResult, mathResult, binarySearchResult, 
                         dpTime, mathTime, binarySearchTime);
    }

    private static int solveDynamicProgramming(int k, int n) {
        // Create a 2D DP table where dp[i][j] represents minimum attempts
        // needed with i eggs and j floors
        int[][] dp = new int[k + 1][n + 1];

        // Base cases
        // With 1 egg, we need to try all floors from bottom to top
        for (int j = 1; j <= n; j++) {
            dp[1][j] = j;
        }

        // With 1 floor, we need only 1 attempt regardless of eggs
        for (int i = 1; i <= k; i++) {
            dp[i][1] = 1;
        }

        // Fill the DP table for i eggs and j floors
        for (int i = 2; i <= k; i++) {
            for (int j = 2; j <= n; j++) {
                dp[i][j] = Integer.MAX_VALUE;

                // Try dropping egg from each floor x (1 to j)
                for (int x = 1; x <= j; x++) {
                    // If egg breaks: we have (i-1) eggs and (x-1) floors below
                    // If egg doesn't break: we have i eggs and (j-x) floors above
                    // We take the worst case (maximum) and add 1 for current attempt
                    int attempts = 1 + Math.max(dp[i - 1][x - 1], dp[i][j - x]);
                    dp[i][j] = Math.min(dp[i][j], attempts);
                }
            }
        }

        return dp[k][n];
    }

    private static int solveMathematical(int k, int n) {
        // Mathematical approach: find minimum m such that
        // sum of C(m, i) for i from 1 to k >= n
        // This represents the maximum floors we can handle with m attempts

        for (int m = 1; m <= n; m++) {
            long totalFloors = 0;

            // Calculate sum of binomial coefficients C(m, i) for i = 1 to k
            for (int i = 1; i <= k && i <= m; i++) {
                totalFloors += binomialCoefficient(m, i);
                if (totalFloors >= n) {
                    return m;
                }
            }

            if (totalFloors >= n) {
                return m;
            }
        }

        return n; // Fallback, should not reach here for valid inputs
    }

    private static int solveBinarySearchWithMemoization(int k, int n) {
        Map<String, Integer> memo = new HashMap<>();
        return binarySearchHelper(k, n, memo);
    }

    private static int binarySearchHelper(int k, int n, Map<String, Integer> memo) {
        // Base cases
        if (n == 0 || n == 1) return n;
        if (k == 1) return n;

        String key = k + "," + n;
        if (memo.containsKey(key)) {
            return memo.get(key);
        }

        int minAttempts = Integer.MAX_VALUE;

        // Use binary search to find optimal floor to drop egg from
        int low = 1, high = n;

        while (low <= high) {
            int mid = low + (high - low) / 2;

            // If egg breaks at floor mid
            int breakCase = binarySearchHelper(k - 1, mid - 1, memo);
            // If egg doesn't break at floor mid
            int noBreakCase = binarySearchHelper(k, n - mid, memo);

            // Current attempt count
            int currentAttempts = 1 + Math.max(breakCase, noBreakCase);

            if (breakCase > noBreakCase) {
                // Break case is worse, try lower floors
                high = mid - 1;
            } else {
                // No-break case is worse, try higher floors
                low = mid + 1;
            }

            minAttempts = Math.min(minAttempts, currentAttempts);
        }

        memo.put(key, minAttempts);
        return minAttempts;
    }

    private static long binomialCoefficient(int n, int k) {
        if (k > n || k < 0) return 0;
        if (k == 0 || k == n) return 1;

        // Use the property C(n,k) = C(n,n-k) to minimize calculations
        k = Math.min(k, n - k);

        long result = 1;
        for (int i = 0; i < k; i++) {
            result = result * (n - i) / (i + 1);
        }

        return result;
    }
}

