            this.isEndOfWord = false;
            this.children = new TrieNode[0]; // start with empty array
        }

        public void addChild(TrieNode child) {
            this.children = insert(this.children, child);
        }

        private TrieNode[] insert(TrieNode[] array, TrieNode element) {
            // Find the position to insert the element to maintain sorted order
            int insertPos = 0;
            while (insertPos < array.length && array[insertPos].character < element.character) {
                insertPos++;
            }

            // Create new array with one more element
            TrieNode[] newArray = new TrieNode[array.length + 1];

            // Copy elements before insertion point
            System.arraycopy(array, 0, newArray, 0, insertPos);

            // Insert the new element
            newArray[insertPos] = element;

            // Copy elements after insertion point
            System.arraycopy(array, insertPos, newArray, insertPos + 1, array.length - insertPos);

            return newArray;
        }

        public TrieNode getChild(char c) {
            // Binary search for the child with character c
            int left = 0, right = children.length - 1;
            while (left <= right) {
                int mid = left + (right - left) / 2;
                if (children[mid].character == c) {
                    return children[mid];
                } else if (children[mid].character < c) {
                    left = mid + 1;
                } else {
                    right = mid - 1;
                }
            }
            return null;
        }
    }

    private TrieNode root;

    public EnhancedTrie() {
        root = new TrieNode('\0'); // root has null character
    }

