        trie.add("dcba"); // Add in reverse order to test sorting

        // Access the root node
        Field rootField = EnhancedTrie.class.getDeclaredField("root");
        rootField.setAccessible(true);
        Object root = rootField.get(trie);

        // Get the TrieNode class
        Class<?> trieNodeClass = root.getClass();
        Field childrenField = trieNodeClass.getDeclaredField("children");
        childrenField.setAccessible(true);

        // Check root's children (should have 'd')
        Object[] rootChildren = (Object[]) childrenField.get(root);
        assertEquals(1, rootChildren.length);

        Field charField = trieNodeClass.getDeclaredField("character");
        charField.setAccessible(true);
        assertEquals('d', charField.get(rootChildren[0]));

        // Navigate to 'd' node and check its children (should have 'c')
        Object dNode = rootChildren[0];
        Object[] dChildren = (Object[]) childrenField.get(dNode);
        assertEquals(1, dChildren.length);
        assertEquals('c', charField.get(dChildren[0]));
    }

    @Test
    public void testAddChildMethodExists() throws Exception {
        EnhancedTrie trie = new EnhancedTrie();
        Field rootField = EnhancedTrie.class.getDeclaredField("root");
        rootField.setAccessible(true);
        Object root = rootField.get(trie);

        Class<?> trieNodeClass = root.getClass();
        Method addChildMethod = trieNodeClass.getDeclaredMethod("addChild", trieNodeClass);
        assertNotNull(addChildMethod);
    }

