        EnhancedTrie trie = new EnhancedTrie();
        Field rootField = EnhancedTrie.class.getDeclaredField("root");
        rootField.setAccessible(true);
        Object root = rootField.get(trie);

        Class<?> trieNodeClass = root.getClass();
        Method insertMethod = trieNodeClass.getDeclaredMethod("insert", trieNodeClass.arrayType(), trieNodeClass);
        assertNotNull(insertMethod);
    }

    @Test
    public void testMultipleChildrenSortedOrder() throws Exception {
        EnhancedTrie trie = new EnhancedTrie();
        trie.add("z");
        trie.add("a");
        trie.add("m");

        Field rootField = EnhancedTrie.class.getDeclaredField("root");
        rootField.setAccessible(true);
        Object root = rootField.get(trie);

        Class<?> trieNodeClass = root.getClass();
        Field childrenField = trieNodeClass.getDeclaredField("children");
        childrenField.setAccessible(true);
        Field charField = trieNodeClass.getDeclaredField("character");
