            current.append(child.character);
            collectWords(child, current, words);
            current.deleteCharAt(current.length() - 1);
        }
    }

    public List<String> getWordsWithPrefix(String prefix) {
        if (prefix == null || prefix.isEmpty()) {
            return new ArrayList<>();
        }

        TrieNode current = root;
        for (char c : prefix.toCharArray()) {
            TrieNode child = current.getChild(c);
            if (child == null) {
                return new ArrayList<>(); // prefix not found
            }
            current = child;
        }

        List<String> words = new ArrayList<>();
        collectWordsWithPrefix(current, new StringBuilder(prefix), words);
        return words;
    }

    private void collectWordsWithPrefix(TrieNode node, StringBuilder current, List<String> words) {
        if (node.isEndOfWord) {
            words.add(current.toString());
        }

        for (TrieNode child : node.children) {
            current.append(child.character);
            collectWordsWithPrefix(child, current, words);
            current.deleteCharAt(current.length() - 1);
        }
    }
}

