import java.util.*;

class AdvancedFlagProcessor {
    private final Set<String> validFlags;
    private final Set<String> mutuallyExclusiveGroups;

    public AdvancedFlagProcessor(Set<String> validFlags, Set<String> mutuallyExclusiveGroups) {
        this.validFlags = new HashSet<>(validFlags);
        this.mutuallyExclusiveGroups = new HashSet<>(mutuallyExclusiveGroups);
    }

    public Set<String> processFlags(String flagString) {
        Set<String> result = new HashSet<>();
        if (flagString == null || flagString.trim().isEmpty()) {
            for (String flag : validFlags) {
                Set<String> flagSet = new HashSet<>();
                flagSet.add(flag);
                if (!violatesMutualExclusivity(flagSet)) {
                    result.add("|" + flag);
                }
            }
        } else {
            // Parse the input flags
            Set<String> inputFlags = new HashSet<>();
            String[] flags = flagString.split("\\|");
            for (String flag : flags) {
                if (!flag.isEmpty()) {
                    inputFlags.add(flag);
                }
            }

            // Validate all flags are in validFlags
            for (String flag : inputFlags) {
                if (!validFlags.contains(flag)) {
                    throw new IllegalArgumentException("Invalid flag: " + flag);
                }
            }

            // Check for mutually exclusive flags in input
            checkMutualExclusivity(inputFlags);

            // Generate new combinations by adding each valid remaining flag
            for (String flag : validFlags) {
                if (!inputFlags.contains(flag)) {
                    Set<String> newCombination = new HashSet<>(inputFlags);
                    newCombination.add(flag);

                    if (!violatesMutualExclusivity(newCombination)) {
                        List<String> sortedFlags = new ArrayList<>(newCombination);
                        Collections.sort(sortedFlags);
                        result.add("|" + String.join("|", sortedFlags));
                    }
                }
            }
        }
        return result;
    }

    private boolean violatesMutualExclusivity(Set<String> flags) {
        Set<String> foundMutuallyExclusiveFlags = new HashSet<>();
        for (String flag : flags) {
            if (mutuallyExclusiveGroups.contains(flag)) {
                foundMutuallyExclusiveFlags.add(flag);
            }
        }
        return foundMutuallyExclusiveFlags.size() > 1;
    }

    private void checkMutualExclusivity(Set<String> flags) {
        Set<String> foundMutuallyExclusiveFlags = new HashSet<>();
        for (String flag : flags) {
            if (mutuallyExclusiveGroups.contains(flag)) {
                foundMutuallyExclusiveFlags.add(flag);
            }
        }

        if (foundMutuallyExclusiveFlags.size() > 1) {
            throw new IllegalArgumentException("Mutually exclusive flags detected");
        }
    }
}

