        List<Integer> currentPath = new ArrayList<>();
        traverseTree(root, currentPath, result, targetSum, 1, 0);

        return result;
    }

    private void traverseTree(TreeNode node, List<Integer> currentPath, 
                            TreeAnalysisResult result, Integer targetSum, int depth, int currentSum) {
        if (node == null) {
            return;
        }

        currentPath.add(node.val);
        currentSum += node.val;

        // Update max depth
        result.maxDepth = Math.max(result.maxDepth, depth);

        if (node.left == null && node.right == null) {
            result.paths.add(new ArrayList<>(currentPath));
            result.leafCount++; // Count leaf nodes

            // Check if this path sum equals target sum
            if (targetSum != null && currentSum == targetSum) {
                result.hasPathSum = true;
            }
        }

        traverseTree(node.left, currentPath, result, targetSum, depth + 1, currentSum);
        traverseTree(node.right, currentPath, result, targetSum, depth + 1, currentSum);

        currentPath.remove(currentPath.size() - 1);
    }
}

