import org.junit.jupiter.api.Test;

class TestTreePathAnalyzer {

    @Test
    public void testPathSumFound() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();

        // Tree: 5 -> 4 -> 11 -> 7, 2
        //       5 -> 8 -> 13, 4 -> 5, 1
        TreeNode root = new TreeNode(5);
        root.left = new TreeNode(4);
        root.right = new TreeNode(8);
        root.left.left = new TreeNode(11);
        root.left.left.left = new TreeNode(7);
        root.left.left.right = new TreeNode(2);
        root.right.left = new TreeNode(13);
        root.right.right = new TreeNode(4);
        root.right.right.right = new TreeNode(1);

        TreeAnalysisResult result = analyzer.analyzeTreePaths(root, 22);
        assertTrue(result.hasPathSum, "Should find path sum of 22 (5+4+11+2)");
    }

    @Test
    public void testPathSumNotFound() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();

        TreeNode root = new TreeNode(1);
        root.left = new TreeNode(2);
        root.right = new TreeNode(3);

        TreeAnalysisResult result = analyzer.analyzeTreePaths(root, 10);
        assertFalse(result.hasPathSum, "Should not find path sum of 10");
    }

    @Test
    public void testNullTargetSum() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();

        TreeNode root = new TreeNode(1);
        root.left = new TreeNode(2);

        TreeAnalysisResult result = analyzer.analyzeTreePaths(root, null);
        assertFalse(result.hasPathSum, "hasPathSum should remain false when targetSum is null");
    }

