    public void testEmptyTreePathSum() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();

        TreeAnalysisResult result = analyzer.analyzeTreePaths(null, 5);
        assertFalse(result.hasPathSum, "Empty tree should not have any path sum");
    }
}

