import java.util.ArrayList;
import java.util.List;
import java.util.HashMap;
import java.util.Map;

class EmojiManager {
    private List<EmojiCategory> categories;
    private EmojiCategory currentCategory;
    private int currentCategoryIndex;
    private Map<String, String> customEmojiResources;

    public EmojiManager(boolean enableRecent, boolean enableCustom, boolean enableSystem) {
        this.categories = new ArrayList<>();
        this.currentCategoryIndex = -1;
        this.customEmojiResources = new HashMap<>();

        if (enableRecent) {
            addRecentEmojiCategory();
        }
        if (enableCustom) {
            addCustomEmojiCategory();
        }
        if (enableSystem) {
            addSystemEmojiCategory();
        }

        // Set current category to first available category
        if (!categories.isEmpty()) {
            this.currentCategoryIndex = 0;
            this.currentCategory = categories.get(0);
        } else {
            this.currentCategory = null;
        }
    }

    public void addRecentEmojiCategory() {
        EmojiCategory recentCategory = new EmojiCategory("Recent", new ArrayList<>());
        categories.add(recentCategory);
    }

    public void addCustomEmojiCategory() {
        EmojiCategory customCategory = new EmojiCategory("Custom", new ArrayList<>());
        categories.add(customCategory);
    }

    public void addSystemEmojiCategory() {
        List<Emoji> systemEmojis = generateDefaultSystemEmojis();
        EmojiCategory systemCategory = new EmojiCategory("System", systemEmojis);
        categories.add(systemCategory);
    }

    public List<Emoji> generateDefaultSystemEmojis() {
        List<Emoji> systemEmojis = new ArrayList<>();
        systemEmojis.add(new Emoji("", "grinning face"));
        systemEmojis.add(new Emoji("", "face with tears of joy"));
        systemEmojis.add(new Emoji("", "red heart"));
        systemEmojis.add(new Emoji("", "thumbs up"));
        systemEmojis.add(new Emoji("", "party popper"));
        return systemEmojis;
    }

    public void addCustomEmojiResource(String name, String resource) {
        customEmojiResources.put(name, resource);

        // Update Custom category if it exists
        for (EmojiCategory category : categories) {
            if ("Custom".equals(category.getName())) {
                category.getEmojis().clear();
                for (Map.Entry<String, String> entry : customEmojiResources.entrySet()) {
                    category.getEmojis().add(new Emoji(entry.getValue(), entry.getKey()));
                }
                break;
            }
        }
    }

    public List<EmojiCategory> getCategories() {
        return categories;
    }

    public int getCategoryCount() {
        return categories.size();
    }

    public int getCurrentCategorySize() {
        if (currentCategory == null) {
            return 0;
        }
        return currentCategory.getEmojiCount();
    }

    public int getTotalEmojiCount() {
        int total = 0;
        for (EmojiCategory category : categories) {
            total += category.getEmojiCount();
        }
        return total;
    }

    public void setCurrentCategory(int index) {
        if (index >= 0 && index < categories.size()) {
            this.currentCategoryIndex = index;
            this.currentCategory = categories.get(index);
        }
        // Do nothing if index is invalid
    }

    public EmojiCategory getCategory(int index) {
        if (index >= 0 && index < categories.size()) {
            return categories.get(index);
        }
        return null; // Return null for invalid indices
    }

    public List<Emoji> getEmojisInRange(int startIndex, int count) {
        if (currentCategory == null) {
            return new ArrayList<>();
        }

        List<Emoji> emojis = currentCategory.getEmojis();
        List<Emoji> result = new ArrayList<>();

        // Handle edge cases
        if (startIndex < 0 || startIndex >= emojis.size() || count <= 0) {
            return result;
        }

        int endIndex = Math.min(startIndex + count, emojis.size());
        for (int i = startIndex; i < endIndex; i++) {
            result.add(emojis.get(i));
        }

        return result;
    }

    public void addToRecent(Emoji emoji) {
        // Find the Recent category
        EmojiCategory recentCategory = null;
        for (EmojiCategory category : categories) {
            if ("Recent".equals(category.getName())) {
                recentCategory = category;
                break;
            }
        }

        if (recentCategory == null) {
            return; // No Recent category exists
        }

        List<Emoji> recentEmojis = recentCategory.getEmojis();

        // Remove emoji if it already exists
        recentEmojis.remove(emoji);

        // Add emoji to the beginning
        recentEmojis.add(0, emoji);

        // Limit to 20 emojis by removing oldest (from the end)
        while (recentEmojis.size() > 20) {
            recentEmojis.remove(recentEmojis.size() - 1);
        }
    }

    public static class EmojiCategory {
        private String name;
        private List<Emoji> emojis;

        public EmojiCategory(String name, List<Emoji> emojis) {
            this.name = name;
            this.emojis = emojis;
        }

        public String getName() {
            return name;
        }

        public List<Emoji> getEmojis() {
            return emojis;
        }

        public int getEmojiCount() {
            return emojis.size();
        }
    }

    public static class Emoji {
        private String character;
        private String description;

        public Emoji(String character, String description) {
            this.character = character;
            this.description = description;
        }

        public String getCharacter() {
            return character;
        }

        public String getDescription() {
            return description;
        }

        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (obj == null || getClass() != obj.getClass()) return false;
            Emoji emoji = (Emoji) obj;
            return character.equals(emoji.character);
        }

        @Override
        public int hashCode() {
            return character.hashCode();
        }
    }
}

