class ItemCatService {
    private Map<Integer, Category> categories = new HashMap<>();

    public void addCategory(int id, String name, int parentId) {
        Category category = new Category(id, name, parentId);
        categories.put(id, category);
    }

    public CatResult getItemCatList() {
        if (categories.isEmpty()) {
            return new CatResult();
        }

        List<CategoryNode> rootNodes = new ArrayList<>();

        // Find root categories (parentId = 0)
        for (Category category : categories.values()) {
            if (category.getParentId() == 0) {
                CategoryNode rootNode = new CategoryNode(category.getId(), category.getName());

                // Find direct children
                List<CategoryNode> children = new ArrayList<>();
                for (Category child : categories.values()) {
                    if (child.getParentId() == category.getId()) {
                        children.add(new CategoryNode(child.getId(), child.getName()));
                    }
                }
                rootNode.setChildren(children);
                rootNodes.add(rootNode);
            }
        }

        return new CatResult(rootNodes);
    }
