        CategoryNode laptops = computers.getChildren().get(0);
        assertEquals(3, laptops.getCatId());
        assertEquals("Laptops", laptops.getCatName());
        assertEquals(1, laptops.getChildren().size());

        CategoryNode gamingLaptops = laptops.getChildren().get(0);
        assertEquals(4, gamingLaptops.getCatId());
        assertEquals("Gaming Laptops", gamingLaptops.getCatName());
