import java.util.*;

class ItemCatService {
    private Map<Integer, Category> categories = new HashMap<>();
    private List<Integer> additionOrder = new ArrayList<>();

    public void addCategory(int id, String name, int parentId) {
        Category category = new Category(id, name, parentId);
        categories.put(id, category);
        additionOrder.add(id);
    }

    public CatResult getItemCatList() {
        if (categories.isEmpty()) {
            return new CatResult();
        }

        List<CategoryNode> rootNodes = new ArrayList<>();

        // Find root categories in the order they were added
        for (Integer categoryId : additionOrder) {
            Category category = categories.get(categoryId);
            if (category.getParentId() == 0) {
                CategoryNode rootNode = buildCategoryTree(category.getId());
                if (rootNode != null) {
                    rootNodes.add(rootNode);
                }
            }
        }

        return new CatResult(rootNodes);
    }

    private CategoryNode buildCategoryTree(int categoryId) {
        Category category = categories.get(categoryId);

        // Skip if parent doesn't exist (except for root categories with parentId = 0)
        if (category.getParentId() != 0 && !categories.containsKey(category.getParentId())) {
            return null;
        }

        CategoryNode node = new CategoryNode(category.getId(), category.getName());

        // Find all direct children and recursively build their trees
        List<CategoryNode> children = new ArrayList<>();
        for (Category child : categories.values()) {
            if (child.getParentId() == categoryId) {
                CategoryNode childNode = buildCategoryTree(child.getId());
                if (childNode != null) {
                    children.add(childNode);
                }
            }
        }
        node.setChildren(children);

        return node;
    }
}

class Category {
    private int id;
    private String name;
    private int parentId;

    public Category(int id, String name, int parentId) {
