import java.util.Comparator;

class EnhancedMergeSort {

    public Integer[] sortIntegers(Integer[] array) {
        // Handle null array
        if (array == null) {
            throw new IllegalArgumentException("Array cannot be null");
        }

        // Handle arrays with 0 or 1 element
        if (array.length <= 1) {
            return array;
        }

        // Divide and conquer
        return mergeSort(array);
    }

    private Integer[] mergeSort(Integer[] array) {
        if (array.length <= 1) {
            return array;
        }

        // Split the array into two halves
        int mid = array.length / 2;
        Integer[] left = new Integer[mid];
        Integer[] right = new Integer[array.length - mid];

        System.arraycopy(array, 0, left, 0, mid);
        System.arraycopy(array, mid, right, 0, array.length - mid);

        // Recursively sort both halves
        left = mergeSort(left);
        right = mergeSort(right);

        // Merge the sorted halves
        return merge(left, right);
    }

    private Integer[] merge(Integer[] left, Integer[] right) {
        Integer[] result = new Integer[left.length + right.length];
        int i = 0, j = 0, k = 0;

        // Merge elements in ascending order
        while (i < left.length && j < right.length) {
            if (left[i] <= right[j]) {
                result[k++] = left[i++];
            } else {
                result[k++] = right[j++];
            }
        }

        // Copy remaining elements from left array
        while (i < left.length) {
            result[k++] = left[i++];
        }

        // Copy remaining elements from right array
        while (j < right.length) {
            result[k++] = right[j++];
        }

        return result;
    }
}

