        Integer[] right = new Integer[array.length - mid];

        System.arraycopy(array, 0, left, 0, mid);
        System.arraycopy(array, mid, right, 0, array.length - mid);

        // Recursively sort both halves
        left = mergeSort(left);
        right = mergeSort(right);

        // Merge the sorted halves
        return merge(left, right);
    }

    @SuppressWarnings("unchecked")
    private <T> T[] genericMergeSort(T[] array, Comparator<? super T> comparator, boolean ascending) {
        if (array.length <= 1) {
            return array;
        }

        // Split the array into two halves
        int mid = array.length / 2;
        T[] left = (T[]) java.lang.reflect.Array.newInstance(array.getClass().getComponentType(), mid);
        T[] right = (T[]) java.lang.reflect.Array.newInstance(array.getClass().getComponentType(), array.length - mid);

        System.arraycopy(array, 0, left, 0, mid);
        System.arraycopy(array, mid, right, 0, array.length - mid);

        // Recursively sort both halves
        left = genericMergeSort(left, comparator, ascending);
        right = genericMergeSort(right, comparator, ascending);

        // Merge the sorted halves
        return genericMerge(left, right, comparator, ascending);
    }

    private Integer[] merge(Integer[] left, Integer[] right) {
        Integer[] result = new Integer[left.length + right.length];
        int i = 0, j = 0, k = 0;

        // Merge elements in ascending order
        while (i < left.length && j < right.length) {
            if (left[i] <= right[j]) {
                result[k++] = left[i++];
            } else {
                result[k++] = right[j++];
            }
        }

        // Copy remaining elements from left array
        while (i < left.length) {
            result[k++] = left[i++];
        }

        // Copy remaining elements from right array
        while (j < right.length) {
            result[k++] = right[j++];
        }

        return result;
    }

    @SuppressWarnings("unchecked")
    private <T> T[] genericMerge(T[] left, T[] right, Comparator<? super T> comparator, boolean ascending) {
        T[] result = (T[]) java.lang.reflect.Array.newInstance(left.getClass().getComponentType(), left.length + right.length);
        int i = 0, j = 0, k = 0;

        // Merge elements based on comparator and ascending flag
        while (i < left.length && j < right.length) {
            int comparison = comparator.compare(left[i], right[j]);
            if (ascending ? comparison <= 0 : comparison >= 0) {
                result[k++] = left[i++];
            } else {
                result[k++] = right[j++];
            }
        }

        // Copy remaining elements from left array
        while (i < left.length) {
            result[k++] = left[i++];
        }

        // Copy remaining elements from right array
        while (j < right.length) {
            result[k++] = right[j++];
        }

        return result;
