import static org.junit.jupiter.api.Assertions.*;
import java.util.Comparator;
import org.junit.jupiter.api.Test;
import java.lang.reflect.Method;
import java.lang.reflect.Modifier;

class TestEnhancedMergeSort {

    @Test
    public void testSingleGenericMergeHelperMethodExists() {
        // Verify that there is exactly one merge method that is generic
        Method[] methods = EnhancedMergeSort.class.getDeclaredMethods();

        int mergeMethodCount = 0;
        Method genericMergeMethod = null;

        for (Method method : methods) {
            if (method.getName().equals("merge")) {
                mergeMethodCount++;
                if (method.getTypeParameters().length > 0) {
                    genericMergeMethod = method;
                }
            }
        }

        // Should have exactly one merge method and it should be generic
        assertEquals(1, mergeMethodCount, "Should have exactly one merge method");
        assertNotNull(genericMergeMethod, "The merge method should be generic");
        assertTrue(Modifier.isPrivate(genericMergeMethod.getModifiers()), "Merge method should be private");

        // Verify the method signature has the expected parameters
        Class<?>[] paramTypes = genericMergeMethod.getParameterTypes();
        assertEquals(5, paramTypes.length, "Merge method should have 5 parameters: left, right, target, comparator, ascending");
    }

    @Test
    public void testUnifiedMergeBehaviorConsistency() {
        EnhancedMergeSort sorter = new EnhancedMergeSort();

        // Test that both sortIntegers and sort produce identical results for the same data
        Integer[] intArray = {5, 2, 8, 1, 9, 3};
        Integer[] result1 = sorter.sortIntegers(intArray.clone());
        Integer[] result2 = sorter.sort(intArray.clone(), Integer::compareTo, true);

        assertArrayEquals(result1, result2, "Both methods should produce identical results when using the same merge logic");
    }

    @Test
    public void testStabilityConsistencyBetweenMethods() {
        EnhancedMergeSort sorter = new EnhancedMergeSort();

        // Create a scenario where stability matters
        class StableTestElement {
            int value;
            int id;

            StableTestElement(int value, int id) {
                this.value = value;
                this.id = id;
            }

            @Override
            public boolean equals(Object obj) {
                if (this == obj) return true;
                if (obj == null || getClass() != obj.getClass()) return false;
                StableTestElement that = (StableTestElement) obj;
                return value == that.value && id == that.id;
            }
        }

        // Test with Integer array that has duplicates
        Integer[] intArray = {3, 1, 3, 1, 2};
        Integer[] intResult = sorter.sortIntegers(intArray);

        // Test with generic array that has duplicates
        StableTestElement[] genericArray = {
            new StableTestElement(3, 1),
            new StableTestElement(1, 2), 
            new StableTestElement(3, 3),
            new StableTestElement(1, 4),
            new StableTestElement(2, 5)
        };

        Comparator<StableTestElement> comp = (a, b) -> Integer.compare(a.value, b.value);
        StableTestElement[] genericResult = sorter.sort(genericArray, comp, true);

        // Both should maintain stable ordering
        assertArrayEquals(new Integer[]{1, 1, 2, 3, 3}, intResult);

        // Verify stable ordering in generic result
        assertEquals(1, genericResult[0].value);
        assertEquals(2, genericResult[0].id); // First 1 should have id 2
        assertEquals(1, genericResult[1].value);
        assertEquals(4, genericResult[1].id); // Second 1 should have id 4
        assertEquals(3, genericResult[3].value);
        assertEquals(1, genericResult[3].id); // First 3 should have id 1
        assertEquals(3, genericResult[4].value);
        assertEquals(3, genericResult[4].id); // Second 3 should have id 3
    }

    @Test
    public void testNoSeparateIntegerSpecificMergeMethod() {
        // Verify that there's no separate Integer-specific merge method
        Method[] methods = EnhancedMergeSort.class.getDeclaredMethods();

        for (Method method : methods) {
            if (method.getName().equals("merge")) {
                // The merge method should be generic, not Integer-specific
                Class<?>[] paramTypes = method.getParameterTypes();
                boolean hasIntegerSpecificSignature = paramTypes.length == 2 && 
                    paramTypes[0] == Integer[].class && 
                    paramTypes[1] == Integer[].class;

                assertFalse(hasIntegerSpecificSignature, 
                    "Should not have a separate Integer-specific merge method");
            }
        }
    }
}

