        String[] input = {"hello"};
        String[] result = sorter.sortStrings(input);
        String[] expected = {"hello"};
        assertArrayEquals(expected, result);
    }

    @Test
    public void testSortStringsNull() {
        EnhancedMergeSort sorter = new EnhancedMergeSort();
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            sorter.sortStrings(null);
        });
        assertEquals("Array must not be null", exception.getMessage());
    }

    @Test
    public void testSortIntegersUsesGenericSort() {
        EnhancedMergeSort sorter = new EnhancedMergeSort();
        Integer[] input = {5, 2, 8, 1};
        Integer[] result = sorter.sortIntegers(input);
        Integer[] expected = {1, 2, 5, 8};
        assertArrayEquals(expected, result);
    }

    @Test
    public void testSortStringsWithDuplicates() {
        EnhancedMergeSort sorter = new EnhancedMergeSort();
        String[] input = {"cat", "apple", "cat", "banana"};
        String[] result = sorter.sortStrings(input);
        String[] expected = {"apple", "banana", "cat", "cat"};
