        String[] input = {"Zebra", "apple", "Banana"};
        String[] result = sorter.sortStrings(input);
        // Natural ordering is case-sensitive: uppercase comes before lowercase
        String[] expected = {"Banana", "Zebra", "apple"};
        assertArrayEquals(expected, result);
    }

    @Test
    public void testSortIntegersInternallyUsesGenericSort() {
        // Create a test subclass that tracks calls to the generic sort method
        class TestableEnhancedMergeSort extends EnhancedMergeSort {
            private boolean sortMethodCalled = false;
            private Comparator<?> usedComparator = null;
            private boolean usedAscending = false;

            @Override
            public <T> T[] sort(T[] array, Comparator<? super T> comparator, boolean ascending) {
                sortMethodCalled = true;
                usedComparator = comparator;
                usedAscending = ascending;
                return super.sort(array, comparator, ascending);
            }

            public boolean wasSortMethodCalled() {
                return sortMethodCalled;
            }

            public Comparator<?> getUsedComparator() {
                return usedComparator;
            }

            public boolean getUsedAscending() {
                return usedAscending;
            }
        }

        TestableEnhancedMergeSort sorter = new TestableEnhancedMergeSort();
        Integer[] input = {3, 1, 2};
        Integer[] result = sorter.sortIntegers(input);

        // Verify that sortIntegers internally calls the generic sort method
        assertTrue(sorter.wasSortMethodCalled(), "sortIntegers should internally use the generic sort method");

        // Verify it uses natural order comparator and ascending=true
        assertEquals(Comparator.naturalOrder(), sorter.getUsedComparator(), "sortIntegers should use Comparator.naturalOrder()");
        assertTrue(sorter.getUsedAscending(), "sortIntegers should use ascending=true");

        // Verify the result is still correct
        Integer[] expected = {1, 2, 3};
        assertArrayEquals(expected, result);
    }
}

