import java.util.*;
import java.net.URLEncoder;
import java.io.UnsupportedEncodingException;

class SecurePaymentProcessor {

    public String generateSecurePaymentUrl(String baseUrl, Map<String, String> parameters) {
        if (parameters == null || parameters.isEmpty()) {
            return baseUrl;
        }

        StringBuilder urlBuilder = new StringBuilder(baseUrl);
        urlBuilder.append("?");

        // Sort parameters by key alphabetically
        TreeMap<String, String> sortedParams = new TreeMap<>(parameters);

        boolean first = true;
        for (Map.Entry<String, String> entry : sortedParams.entrySet()) {
            if (!first) {
                urlBuilder.append("&");
            }

            try {
                String encodedKey = URLEncoder.encode(entry.getKey(), "UTF-8");
                String encodedValue = URLEncoder.encode(entry.getValue(), "UTF-8");
                urlBuilder.append(encodedKey).append("=").append(encodedValue);
            } catch (UnsupportedEncodingException e) {
                // UTF-8 is always supported, but we need to handle the exception
                throw new RuntimeException("UTF-8 encoding not supported", e);
            }

            first = false;
        }

        return urlBuilder.toString();
    }
}

