        "A8", "A9", "AA", "AB", "AC", "AD", "AE", "AF",
        "B0", "B1", "B2", "B3", "B4", "B5", "B6", "B7",
        "B8", "B9", "BA", "BB", "BC", "BD", "BE", "BF",
        "C0", "C1", "C2", "C3", "C4", "C5", "C6", "C7",
        "C8", "C9", "CA", "CB", "CC", "CD", "CE", "CF",
        "D0", "D1", "D2", "D3", "D4", "D5", "D6", "D7",
        "D8", "D9", "DA", "DB", "DC", "DD", "DE", "DF",
        "E0", "E1", "E2", "E3", "E4", "E5", "E6", "E7",
        "E8", "E9", "EA", "EB", "EC", "ED", "EE", "EF",
        "F0", "F1", "F2", "F3", "F4", "F5", "F6", "F7",
        "F8", "F9", "FA", "FB", "FC", "FD", "FE", "FF"
    };

    public String generateSecurePaymentUrl(String baseUrl, Map<String, String> params, String secret) {
        // Input validation
        if (baseUrl == null || baseUrl.trim().isEmpty()) {
            throw new IllegalArgumentException("baseUrl cannot be null or empty");
        }

        if (secret == null || secret.isEmpty()) {
            throw new IllegalArgumentException("Secret key must not be null or empty");
        }

        if (params == null || params.isEmpty()) {
            return baseUrl;
        }

        // Create a copy to avoid modifying the original map
        Map<String, String> parameters = new HashMap<>(params);

        // Generate secure hash
        String secureHash = generateSecureHash(secret, parameters);

        // Add the hash to parameters
        parameters.put("vpc_SecureHash", secureHash);

        StringBuilder urlBuilder = new StringBuilder(baseUrl);
        urlBuilder.append("?");

        // Sort parameters by key alphabetically
        TreeMap<String, String> sortedParams = new TreeMap<>(parameters);

        boolean first = true;
        for (Map.Entry<String, String> entry : sortedParams.entrySet()) {
            String value = entry.getValue();
            // Skip parameters with null or empty values
            if (value == null || value.isEmpty()) {
                continue;
            }

            if (!first) {
                urlBuilder.append("&");
            }

            try {
                String encodedKey = URLEncoder.encode(entry.getKey(), "UTF-8");
                String encodedValue = URLEncoder.encode(value, "UTF-8");
                urlBuilder.append(encodedKey).append("=").append(encodedValue);
            } catch (UnsupportedEncodingException e) {
                // UTF-8 is always supported, but we need to handle the exception
                throw new RuntimeException("UTF-8 encoding not supported", e);
            }

            first = false;
        }

        // If all parameters were empty, remove the trailing "?"
        if (first) {
            urlBuilder.setLength(urlBuilder.length() - 1);
        }

        return urlBuilder.toString();
    }

    private String generateSecureHash(String secret, Map<String, String> parameters) {
        try {
            // Build query string for hash calculation
            StringBuilder queryString = new StringBuilder();

            // Sort parameters by key and build query string
            TreeMap<String, String> sortedParams = new TreeMap<>(parameters);
            boolean first = true;
            for (Map.Entry<String, String> entry : sortedParams.entrySet()) {
                String value = entry.getValue();
                if (value != null && !value.isEmpty()) {
                    if (!first) {
                        queryString.append("&");
                    }
                    queryString.append(entry.getKey()).append("=").append(value);
                    first = false;
                }
            }

            // Calculate HMAC-SHA256
            Mac mac = Mac.getInstance("HmacSHA256");
            SecretKeySpec secretKeySpec = new SecretKeySpec(secret.getBytes("UTF-8"), "HmacSHA256");
            mac.init(secretKeySpec);
            byte[] hashBytes = mac.doFinal(queryString.toString().getBytes("UTF-8"));

            return bytesToHex(hashBytes);
        } catch (NoSuchAlgorithmException | InvalidKeyException | UnsupportedEncodingException e) {
            throw new RuntimeException("Failed to generate secure hash: " + e.getMessage(), e);
        }
    }

    private String bytesToHex(byte[] bytes) {
        StringBuilder hexString = new StringBuilder();
        for (byte b : bytes) {
            int index = b & 0xFF;
            hexString.append(HEX_TABLE[index]);
        }
        return hexString.toString();
    }
}

