        // vpc_SecureHash should be included and URL-encoded
        assertTrue(result.contains("vpc_SecureHash="));
        // Should be properly sorted (amount comes before vpc_SecureHash alphabetically)
        int amountIndex = result.indexOf("amount=");
        int hashIndex = result.indexOf("vpc_SecureHash=");
        assertTrue(amountIndex < hashIndex);
    }

    private String extractHashFromUrl(String url) {
        String[] parts = url.split("vpc_SecureHash=");
        if (parts.length > 1) {
            String hashPart = parts[1];
            int ampIndex = hashPart.indexOf("&");
            return ampIndex > 0 ? hashPart.substring(0, ampIndex) : hashPart;
        }
        return "";
    }
}

