import java.util.*;

class GraphAnalyzer {
    private Map<String, List<Edge>> adjacencyList;
    private boolean directed;
    private boolean weighted;

    public GraphAnalyzer(boolean directed, boolean weighted) {
        this.directed = directed;
        this.weighted = weighted;
        this.adjacencyList = new HashMap<>();
    }

    public void addNode(String node) {
        adjacencyList.putIfAbsent(node, new ArrayList<>());
    }

    public void addEdge(String from, String to, int weight) {
        // Ensure both nodes exist
        addNode(from);
        addNode(to);

        // Add edge from 'from' to 'to'
        adjacencyList.get(from).add(new Edge(to, weight));

        // If undirected, also add the reverse edge
        if (!directed) {
            adjacencyList.get(to).add(new Edge(from, weight));
        }
    }

    public int getNodeDegree(String node) {
        if (!adjacencyList.containsKey(node)) {
            throw new IllegalArgumentException("Node does not exist in the graph");
        }

        int degree = adjacencyList.get(node).size();        
        return degree;
    }

    public boolean hasCycle() {
        Set<String> visited = new HashSet<>();

        for (String node : adjacencyList.keySet()) {
            if (!visited.contains(node)) {
                if (directed) {
                    if (hasCycleDirected(node, visited, new HashSet<>())) {
                        return true;
                    }
                } else {
                    if (hasCycleUndirected(node, visited, null)) {
                        return true;
                    }
                }
            }
        }

        return false;
    }

    private boolean hasCycleDirected(String node, Set<String> visited, Set<String> recursionStack) {
        visited.add(node);
        recursionStack.add(node);

        List<Edge> neighbors = adjacencyList.get(node);
        if (neighbors != null) {
            for (Edge edge : neighbors) {
                String neighbor = edge.destination;

                if (!visited.contains(neighbor)) {
                    if (hasCycleDirected(neighbor, visited, recursionStack)) {
                        return true;
                    }
                } else if (recursionStack.contains(neighbor)) {
                    return true;
                }
            }
        }

        recursionStack.remove(node);
        return false;
    }

    private boolean hasCycleUndirected(String node, Set<String> visited, String parent) {
        visited.add(node);

        List<Edge> neighbors = adjacencyList.get(node);
        if (neighbors != null) {
            for (Edge edge : neighbors) {
                String neighbor = edge.destination;

                if (!visited.contains(neighbor)) {
                    if (hasCycleUndirected(neighbor, visited, node)) {
                        return true;
                    }
                } else if (!neighbor.equals(parent)) {
                    return true;
                }
            }
        }

        return false;
    }

    static class Edge {
        String destination;
        int weight;

        Edge(String destination, int weight) {
            this.destination = destination;
            this.weight = weight;
        }
    }
}

