    public List<String> findShortestPath(String start, String end) {
        if (!adjacencyList.containsKey(start)) {
            throw new IllegalArgumentException("Start node does not exist");
        }
        if (!adjacencyList.containsKey(end)) {
            throw new IllegalArgumentException("End node does not exist");
        }

        // Dijkstra's algorithm
        Map<String, Integer> distances = new HashMap<>();
        Map<String, String> previous = new HashMap<>();
        PriorityQueue<NodeDistance> pq = new PriorityQueue<>(Comparator.comparingInt(nd -> nd.distance));
        Set<String> visited = new HashSet<>();

        // Initialize distances
        for (String node : adjacencyList.keySet()) {
            distances.put(node, Integer.MAX_VALUE);
        }
        distances.put(start, 0);
        pq.add(new NodeDistance(start, 0));

        while (!pq.isEmpty()) {
            NodeDistance current = pq.poll();
            String currentNode = current.node;

            if (visited.contains(currentNode)) {
                continue;
            }

            visited.add(currentNode);

            // If we reached the destination
            if (currentNode.equals(end)) {
                break;
            }

            // Check neighbors
            List<Edge> neighbors = adjacencyList.get(currentNode);
            if (neighbors != null) {
                for (Edge edge : neighbors) {
                    String neighbor = edge.destination;
                    int newDist = distances.get(currentNode) + edge.weight;

                    if (newDist < distances.get(neighbor)) {
                        distances.put(neighbor, newDist);
                        previous.put(neighbor, currentNode);
                        pq.add(new NodeDistance(neighbor, newDist));
                    }
                }
            }
        }

        // If end node is unreachable
        if (distances.get(end) == Integer.MAX_VALUE) {
            return new ArrayList<>();
        }

        // Reconstruct path
        List<String> path = new ArrayList<>();
        String current = end;
        while (current != null) {
            path.add(current);
            current = previous.get(current);
        }
        Collections.reverse(path);

        return path;
    }

    static class Edge {
        String destination;
        int weight;

        Edge(String destination, int weight) {
            this.destination = destination;
            this.weight = weight;
        }
    }

    static class NodeDistance {
        String node;
        int distance;

        NodeDistance(String node, int distance) {
            this.node = node;
            this.distance = distance;
        }
    }
}

