import java.util.*;
import java.util.function.Consumer;

class AlertingService {
    private Map<String, Integer> alertCounts;
    private Map<String, String> lastAlertMessages;
    private Map<String, Consumer<String>> alertListeners;

    public AlertingService() {
        alertCounts = new HashMap<>();
        lastAlertMessages = new HashMap<>();
        alertListeners = new HashMap<>();
    }

    public void triggerAlert(String severity, String message) {
        // Increment alert count for the severity level
        alertCounts.put(severity, alertCounts.getOrDefault(severity, 0) + 1);
        // Update the last alert message for the severity level
        lastAlertMessages.put(severity, message);

        // Notify all registered listeners for this severity level
        for (Map.Entry<String, Consumer<String>> entry : alertListeners.entrySet()) {
            String key = entry.getKey();
            String level = key.substring(key.indexOf(":") + 1);
            if (level.equals(severity)) {
                entry.getValue().accept(message);
            }
        }
    }

    public int getAlertCount(String severity) {
        return alertCounts.getOrDefault(severity, 0);
    }

    public String getLastAlertMessage(String severity) {
        return lastAlertMessages.get(severity);
    }

    public void reset() {
        alertCounts.clear();
        lastAlertMessages.clear();
        alertListeners.clear();
    }

    public void addAlertListener(String listenerId, String level, Consumer<String> callback) {
        String compositeKey = listenerId + ":" + level;
        alertListeners.put(compositeKey, callback);
    }
}

