class TestAlertingService {

    @Test
    public void testThreadSafetyWithConcurrentAlerts() throws InterruptedException {
        AlertingService service = new AlertingService();
        int threadCount = 10;
        int alertsPerThread = 1000;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);

        // Submit multiple threads that trigger alerts
        for (int i = 0; i < threadCount; i++) {
            final int threadId = i;
            executor.submit(() -> {
                for (int j = 0; j < alertsPerThread; j++) {
                    service.triggerAlert("ERROR", "Error from thread " + threadId + " alert " + j);
                }
            });
        }

        executor.shutdown();
        executor.awaitTermination(10, TimeUnit.SECONDS);

        // Verify that the alert count is correct
        assertEquals(threadCount * alertsPerThread, service.getAlertCount("ERROR"));
    }

    @Test
    public void testThreadSafetyWithConcurrentListeners() throws InterruptedException {
        AlertingService service = new AlertingService();
        int threadCount = 5;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        AtomicInteger listenerCounter = new AtomicInteger(0);

        // Add listeners from multiple threads
        for (int i = 0; i < threadCount; i++) {
            final int threadId = i;
            executor.submit(() -> {
                service.addAlertListener("listener" + threadId, "INFO", msg -> {
                    listenerCounter.incrementAndGet();
                });
            });
        }

        executor.shutdown();
        executor.awaitTermination(5, TimeUnit.SECONDS);

        // Trigger an alert and verify all listeners were notified
        service.triggerAlert("INFO", "Test message");

        // Give some time for async notifications
        Thread.sleep(100);

        assertEquals(threadCount, listenerCounter.get());
    }
}

