import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.concurrent.ConcurrentHashMap;
import java.util.function.Consumer;

class TestAlertingService {
    @Test
    public void testAddAlertListenerInitializesAlertLevel() {
        AlertingService service = new AlertingService();

        // Add a listener for a new alert level
        service.addAlertListener("listener1", "INFO", msg -> {});

        // Verify that the alert level is initialized with count 0
        assertEquals(0, service.getAlertCount("INFO"));

        // Verify that the alertCounts map contains the level
        try {
            java.lang.reflect.Field alertCountsField = AlertingService.class.getDeclaredField("alertCounts");
            alertCountsField.setAccessible(true);
            ConcurrentHashMap<String, AtomicInteger> alertCounts = (ConcurrentHashMap<String, AtomicInteger>) alertCountsField.get(service);
            assertTrue(alertCounts.containsKey("INFO"));
            assertEquals(0, alertCounts.get("INFO").get());
        } catch (Exception e) {
            fail("Reflection failed: " + e.getMessage());
        }
    }

    @Test
    public void testTriggerAlertInitializesAlertLevel() {
        AlertingService service = new AlertingService();

        // Trigger an alert for a new level
        service.triggerAlert("WARNING", "This is a warning");

        // Verify that the alert level is initialized and incremented
        assertEquals(1, service.getAlertCount("WARNING"));

        // Verify that the alertCounts map contains the level
        try {
            java.lang.reflect.Field alertCountsField = AlertingService.class.getDeclaredField("alertCounts");
            alertCountsField.setAccessible(true);
            ConcurrentHashMap<String, AtomicInteger> alertCounts = (ConcurrentHashMap<String, AtomicInteger>) alertCountsField.get(service);
            assertTrue(alertCounts.containsKey("WARNING"));
            assertEquals(1, alertCounts.get("WARNING").get());
        } catch (Exception e) {
            fail("Reflection failed: " + e.getMessage());
        }
    }

    @Test
    public void testAddAlertListenerDoesNotOverrideExistingCount() {
        AlertingService service = new AlertingService();

        // Trigger an alert to initialize the count
        service.triggerAlert("ERROR", "First error");
        assertEquals(1, service.getAlertCount("ERROR"));

        // Add a listener for the same level
        service.addAlertListener("listener1", "ERROR", msg -> {});

        // Verify that the count is not reset
        assertEquals(1, service.getAlertCount("ERROR"));
    }

    @Test
    public void testTriggerAlertDoesNotOverrideExistingCount() {
        AlertingService service = new AlertingService();

        // Add a listener to initialize the level
        service.addAlertListener("listener1", "DEBUG", msg -> {});
        assertEquals(0, service.getAlertCount("DEBUG"));

        // Trigger an alert for the same level
        service.triggerAlert("DEBUG", "Debug message");

        // Verify that the count is incremented
        assertEquals(1, service.getAlertCount("DEBUG"));
    }
}

