import java.util.*;
import java.nio.file.*;
import java.io.*;
import java.util.zip.*;

class FileHandler {
    public static Object handleFileOperation(String operationType, List<String> files, String targetDirectory) throws IOException {
        if (operationType == null) {
            throw new IllegalArgumentException("Invalid operation type: null");
        }

        switch (operationType) {
            case "upload":
                return handleUpload(files, targetDirectory);
            case "download":
                return handleDownload(files, targetDirectory);
            case "zip":
                return handleZip(files, targetDirectory);
            default:
                throw new IllegalArgumentException("Invalid operation type: " + operationType);
        }
    }

    private static String handleUpload(List<String> files, String targetDirectory) throws IOException {
        Path targetDirPath = Paths.get(targetDirectory);

        // Create the target directory if it doesn't exist
        if (!Files.exists(targetDirPath)) {
            Files.createDirectories(targetDirPath);
        }

        // Copy all files to the target directory
        int count = 0;
        for (String filePath : files) {
            Path sourcePath = Paths.get(filePath);
            Path fileName = sourcePath.getFileName();
            Path targetPath = targetDirPath.resolve(fileName);
            Files.copy(sourcePath, targetPath, StandardCopyOption.REPLACE_EXISTING);
            count++;
        }

        // Return grammatically correct message
        if (count == 1) {
            return count + " file uploaded successfully";
        } else {
            return count + " files uploaded successfully";
        }
    }

    private static byte[] handleDownload(List<String> files, String targetDirectory) throws IOException {
        Path targetDirPath = Paths.get(targetDirectory);
        String fileName = files.get(0); // Assuming the first file is the one to download
        Path filePath = targetDirPath.resolve(fileName);
        return Files.readAllBytes(filePath);
    }

    private static byte[] handleZip(List<String> files, String targetDirectory) throws IOException {
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        ZipOutputStream zos = new ZipOutputStream(baos);

        for (String filePath : files) {
            Path path = Paths.get(filePath);
            byte[] fileBytes = Files.readAllBytes(path);
            ZipEntry entry = new ZipEntry(path.getFileName().toString());
            zos.putNextEntry(entry);
            zos.write(fileBytes, 0, fileBytes.length);
            zos.closeEntry();
        }

        zos.close();
        return baos.toByteArray();
    }
}

