        }

        @Override
        public void runTest() throws Exception {
            // Do nothing - test passes
        }

        @Override
        public String getTestName() {
            return name;
        }
    }

    private static class FailingTest implements UnitTest {
        private String name;

        public FailingTest(String name) {
            this.name = name;
        }

        @Override
        public void runTest() throws Exception {
            throw new AssertionError("Assertion failed");
        }

        @Override
        public String getTestName() {
            return name;
        }
    }

    private static class ErrorTest implements UnitTest {
        private String name;

        public ErrorTest(String name) {
            this.name = name;
        }

        @Override
        public void runTest() throws Exception {
            throw new RuntimeException("Runtime error");
        }

        @Override
        public String getTestName() {
            return name;
        }
    }

    @org.junit.jupiter.api.Test
    void testCategorization() {
        UnitTestRunner runner = new UnitTestRunner();
        runner.addTest(new PassingTest("testPass"));
        runner.addTest(new FailingTest("testFail"));
        runner.addTest(new ErrorTest("testError"));

        UnitTestRunner.TestResult result = runner.runAllTests();

        assertEquals(1, result.passed);
        assertEquals(1, result.failed);
        assertEquals(1, result.error);

        List<String> expectedDetails = Arrays.asList(
            "testPass PASSED",
            "testFail FAILED: Assertion failed",
            "testError ERROR: Runtime error"
        );

        assertEquals(expectedDetails, result.details);
    }

    @org.junit.jupiter.api.Test
    void testOnlyPassed() {
        UnitTestRunner runner = new UnitTestRunner();
        runner.addTest(new PassingTest("test1"));
        runner.addTest(new PassingTest("test2"));

        UnitTestRunner.TestResult result = runner.runAllTests();

        assertEquals(2, result.passed);
        assertEquals(0, result.failed);
        assertEquals(0, result.error);

        List<String> expectedDetails = Arrays.asList(
            "test1 PASSED",
            "test2 PASSED"
        );

        assertEquals(expectedDetails, result.details);
    }
}

