import java.util.*;

class EnhancedTariffManager {
    private Map<String, Tariff> tariffs;
    private Map<String, Contract> contracts;
    private Map<String, Set<String>> tariffToContracts;

    public EnhancedTariffManager() {
        tariffs = new HashMap<>();
        contracts = new HashMap<>();
        tariffToContracts = new HashMap<>();
    }

    public void addTariff(Tariff tariff) {
        if (tariff == null) {
            throw new IllegalArgumentException();
        }
        if (tariffs.containsKey(tariff.getId())) {
            throw new IllegalArgumentException();
        }
        tariffs.put(tariff.getId(), tariff);
        tariffToContracts.put(tariff.getId(), new HashSet<>());
    }

    public void addContract(Contract contract) {
        contracts.put(contract.getId(), contract);
    }

    public void subscribeContractToTariff(String contractId, String tariffId) {
        if (contractId == null) {
            throw new IllegalArgumentException("Contract cannot be null");
        }
        if (!contracts.containsKey(contractId)) {
            throw new IllegalArgumentException("Contract not found: " + contractId);
        }
        if (!tariffs.containsKey(tariffId)) {
            throw new IllegalArgumentException("Tariff ID not found: " + tariffId);
        }
        tariffToContracts.get(tariffId).add(contractId);
    }

    public Set<Contract> getContractsByTariff(String tariffId) {
        if (!tariffs.containsKey(tariffId)) {
            throw new IllegalArgumentException("Tariff ID not found: " + tariffId);
        }

        Set<String> contractIds = tariffToContracts.get(tariffId);
        Set<Contract> result = new HashSet<>();

        for (String contractId : contractIds) {
            result.add(contracts.get(contractId));
        }

        return result;
    }

    public Tariff getMostPopularTariff() {
        if (tariffs.isEmpty()) {
            return null;
        }

        Tariff mostPopular = null;
        int maxSubscribers = -1;

        for (Map.Entry<String, Tariff> entry : tariffs.entrySet()) {
            String tariffId = entry.getKey();
            Tariff tariff = entry.getValue();
            int subscriberCount = tariffToContracts.get(tariffId).size();

            if (subscriberCount > maxSubscribers) {
                maxSubscribers = subscriberCount;
                mostPopular = tariff;
            }
        }

        return mostPopular;
    }
}

class Tariff {
    private String id;
    private String name;
    private double monthlyFee;

    public Tariff(String id, String name, double monthlyFee) {
        this.id = id;
        this.name = name;
        this.monthlyFee = monthlyFee;
    }

    public String getId() {
        return id;
    }

    public String getName() {
        return name;
    }

    public double getMonthlyFee() {
        return monthlyFee;
    }
}

class Contract {
    private String id;
    private String clientName;

    public Contract(String id, String clientName) {
        this.id = id;
        this.clientName = clientName;
    }

    public String getId() {
        return id;
    }

    public String getClientName() {
        return clientName;
    }
}

