        assertNull(manager.getMostPopularTariff());
    }

    @Test
    void testGetMostPopularTariff_SingleTariffNoSubscribers() {
        EnhancedTariffManager manager = new EnhancedTariffManager();
        Tariff tariff = new Tariff("T1", "Basic", 10.0);
        manager.addTariff(tariff);
        assertEquals(tariff, manager.getMostPopularTariff());
    }

    @Test
    void testGetMostPopularTariff_MultipleTariffsDifferentSubscribers() {
        EnhancedTariffManager manager = new EnhancedTariffManager();

        Tariff tariff1 = new Tariff("T1", "Basic", 10.0);
        Tariff tariff2 = new Tariff("T2", "Premium", 20.0);
        Tariff tariff3 = new Tariff("T3", "VIP", 30.0);

        manager.addTariff(tariff1);
        manager.addTariff(tariff2);
        manager.addTariff(tariff3);

        Contract contract1 = new Contract("C1", "Client1");
        Contract contract2 = new Contract("C2", "Client2");
        Contract contract3 = new Contract("C3", "Client3");
        Contract contract4 = new Contract("C4", "Client4");

        manager.addContract(contract1);
        manager.addContract(contract2);
        manager.addContract(contract3);
        manager.addContract(contract4);

        // Subscribe 0 contracts to T1
        // Subscribe 2 contracts to T2
        manager.subscribeContractToTariff("C1", "T2");
        manager.subscribeContractToTariff("C2", "T2");
        // Subscribe 1 contract to T3
        manager.subscribeContractToTariff("C3", "T3");

        assertEquals(tariff2, manager.getMostPopularTariff());
    }

    @Test
    void testGetMostPopularTariff_Tie() {
        EnhancedTariffManager manager = new EnhancedTariffManager();

        Tariff tariff1 = new Tariff("T1", "Basic", 10.0);
        Tariff tariff2 = new Tariff("T2", "Premium", 20.0);

        manager.addTariff(tariff1);
        manager.addTariff(tariff2);

        Contract contract1 = new Contract("C1", "Client1");
        Contract contract2 = new Contract("C2", "Client2");

        manager.addContract(contract1);
        manager.addContract(contract2);

        // Subscribe 1 contract to each tariff
        manager.subscribeContractToTariff("C1", "T1");
        manager.subscribeContractToTariff("C2", "T2");

        // Should return either tariff1 or tariff2
        Tariff result = manager.getMostPopularTariff();
        assertTrue(result == tariff1 || result == tariff2);
    }
}

