        RoleManager manager = new RoleManager(validRoles, false);

        // allowedRoles is empty by default
        Set<String> userRoles = new HashSet<>(Arrays.asList("user"));
        assertTrue(manager.isAccessAllowed(userRoles));

        // Test with user having no roles
        Set<String> noRoles = new HashSet<>();
        assertTrue(manager.isAccessAllowed(noRoles));

        // Test with user having any role when allowedRoles is empty
        Set<String> adminRoles = new HashSet<>(Arrays.asList("admin"));
        assertTrue(manager.isAccessAllowed(adminRoles));
    }

    @Test
    void testAccessDeniedWhenDisallowedRolePresentAndAllowedRolesEmpty() {
        Set<String> validRoles = new HashSet<>(Arrays.asList("admin", "user", "guest"));
        RoleManager manager = new RoleManager(validRoles, false);

        // Set a disallowed role
        manager.setDisallowedRoles("admin");

        // User has the disallowed role, should be denied even if allowedRoles is empty
        Set<String> userRoles = new HashSet<>(Arrays.asList("admin", "user"));
        assertFalse(manager.isAccessAllowed(userRoles));

        // User has only disallowed role
        Set<String> onlyDisallowed = new HashSet<>(Arrays.asList("admin"));
        assertFalse(manager.isAccessAllowed(onlyDisallowed));
    }

