    void testAccessWithNonEmptyAllowedRoles() {
        Set<String> validRoles = new HashSet<>(Arrays.asList("admin", "user", "guest"));
        RoleManager manager = new RoleManager(validRoles, false);

        // Set allowed roles
        manager.setAllowedRoles("admin,user");

        // User has an allowed role
        Set<String> userRoles = new HashSet<>(Arrays.asList("user"));
        assertTrue(manager.isAccessAllowed(userRoles));

        // User doesn't have an allowed role
        Set<String> guestRoles = new HashSet<>(Arrays.asList("guest"));
        assertFalse(manager.isAccessAllowed(guestRoles));

        // User has no roles
        Set<String> noRoles = new HashSet<>();
        assertFalse(manager.isAccessAllowed(noRoles));
    }
