import java.util.*;

class CourseScheduler {
    static class Result {
        boolean canFinish;
        int[] order;

        Result(boolean canFinish, int[] order) {
            this.canFinish = canFinish;
            this.order = order;
        }
    }

    public Result scheduleCourses(int numCourses, int[][] prerequisites) {
        // Build adjacency list representation of the graph
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < numCourses; i++) {
            graph.add(new ArrayList<>());
        }

        // Calculate in-degrees for each course
        int[] inDegree = new int[numCourses];

        // Process prerequisites to build graph and in-degrees
        for (int[] prereq : prerequisites) {
            int course = prereq[0];
            int prerequisite = prereq[1];

            // Add edge from prerequisite to course
            graph.get(prerequisite).add(course);

            // Increment in-degree of course
            inDegree[course]++;
        }

        // Find courses with no prerequisites (in-degree = 0)
        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < numCourses; i++) {
            if (inDegree[i] == 0) {
                queue.offer(i);
            }
        }

        // For now, just return the courses with no prerequisites as the order
        // In the next round we'll implement the full topological sort
        int[] order = new int[queue.size()];
        int index = 0;
        while (!queue.isEmpty()) {
            order[index++] = queue.poll();
        }

        // If there are no courses with in-degree 0, but there are courses,
        // it means there's a cycle (for now we'll assume it's finishable if there are starting points)
        boolean canFinish = order.length > 0 || numCourses == 0;

        return new Result(canFinish, order);
    }
}

